import org.junit.jupiter.api.*;
import static org.junit.jupiter.api.Assertions.*;

public class LojaTest {
	@Test
	public void tamanhoDaLoja() {
		Loja l = null;
		char expected = 0x0;
		char actual = l.tamanhoDaLoja();

		assertEquals(expected, actual);
	}

	@Test
	public void tamanhoDaLojaTODO1() {
		Loja l = new Loja(null, 0, 0.);
		char expected = 'P';
		char actual = l.tamanhoDaLoja();

		assertEquals(expected, actual);
	}

	@Test
	public void tamanhoDaLojaTODO2() {
		Loja l = new Loja(null, 10, 0.);
		char expected = 'M';
		char actual = l.tamanhoDaLoja();

		assertEquals(expected, actual);
	}
}
